import React, { Component } from "react";
import "./index.css";

export default class Item extends Component {
  state = { mouse: false, isEdit: false }; //鼠标移入移出
  constructor(props) {
    super(props);
    this.textInput = React.createRef();

  }

  //鼠标移入移出
  handleMouse = (flag) => {
    return () => {
      this.setState({ mouse: flag });
    };
  };
  //勾选
  handleCheck = (id) => {
    return (event) => {
      this.props.updateTodo(id, event.target.checked);
    };
  };
  //删除
  handleDelete = (id) => {
    return () => {
      this.props.deleteTodo(id);
    };
  };
  focusTextInput= ()=>{

  }
  //编辑
  handleEdite = () => {
    this.textInput.current.focus();
    return () => {
      this.setState({ isEdit: true });
      //console.log(this.state.isEdit);
      this.props.editTodos();
    };
  };
  //编辑完成
  handleComEdit = (id) => {
    return (event) => {
      this.setState({ isEdit: false });

      this.props.editTodos(id, event.target.value);
    };
  };
  //级别改变
  handleChange=(id)=>{
    return (event) => {

      this.props.changeSign(id, event.target.value);
    };
  }
  componentDidMount(){
    if(isEdit) 
    {   
      console.log('sss');
      this.inputRef.current.focus();}
    }
  render() {
    const { id, name, done ,sign} = this.props;
    const { mouse, isEdit } = this.state;
    return (
      <li
        style={{ backgroundColor: mouse ? "#ddd" : "white" }}
        onMouseEnter={this.handleMouse(true)}
        onMouseLeave={this.handleMouse(false)}
      >
        <input
          type="checkbox"
          defaultChecked={done}
          onChange={this.handleCheck(id)}
        />
        {isEdit ? (
          <input
            className="li-input"
            type="text"
            onBlur={this.handleComEdit(id)}
            defaultValue={name}
            ref={this.inputRef}
          ></input>
        ) : (
          <span onClick={this.handleEdite(id)}>{name}</span>
        )}
        <select className="li-select"
                style={{ display: mouse ? "block" : "none" }}
                onChange={this.handleChange(id)}
                defaultValue={sign}

        >
                      <option value="3">重要</option>
                      <option value="2">一般</option>
                      <option value="1">不重要</option>
        </select>

        <button
          onClick={this.handleDelete(id)}
          className="btn btn-danger"
          style={{ display: mouse ? "block" : "none" }}
        >
          删除
        </button>
      </li>
    );
  }
}
