import React, { Component } from "react";
import Header from "./components/Header";
import List from "./components/List";
import Footer from "./components/Footer";
import "./App.css";
export default class App extends Component {
  //初始化状态
  state = {
    todos: [
      { id: "001", name: "a", done: true ,},
      { id: "002", name: "c", done: true },
      { id: "003", name: "b", done: false },
      { id: "004", name: "d", done: false },
    ],
    filter: "all",
  };
  //添加
  add = (todoObj) => {
    const { todos, showTodos } = this.state;
    const newTodos = [todoObj, ...todos];
    this.setState({ todos: newTodos });
  };
  //更新
  updateTodo = (id, done) => {
    //获取状态中的todos
    const { todos, showTodos } = this.state;
    //匹配处理数据
    const newTodos = todos.map((todoObj) => {
      if (todoObj.id === id) return { ...todoObj, done };
      else return todoObj;
    });

    this.setState({ todos: newTodos });

  };
  //删除
  deleteTodo = (id) => {
    const { todos, showTodos } = this.state;
    const newTodos = todos.filter((todoObj) => {
      return todoObj.id !== id;
    });

    this.setState({ todos: newTodos });

  };

  //删除已完成
  clearAllDone = () => {
    const { todos } = this.state;
    const newTodos = todos.filter((todoObj) => {
      return !todoObj.done;
    });
    this.setState({ todos: newTodos });
  };
  //显示已完成
  allDone = () => {
    this.setState({ filter: 'complete' });
  };
  //全部
  allTodos = () => {
    //console.log('---',this.state.showTodos);
	this.setState({ filter: 'all' });
  };
  //未完成
  allNoDone = () => {
    this.setState({ filter: 'undone' });
  };
  //编辑
  editTodos = (id, name) => {
    const { todos, showTodos } = this.state;
    const newTodos = todos.map((todoObj) => {
      if (todoObj.id === id) return { ...todoObj, name };
      else return todoObj;
    });
    this.setState({ todos: newTodos });

  };

  render() {
    const { todos, showTodos } = this.state;
    return (
      <div className="todo-container">
        <div className="todo-wrap">
          <Header add={this.add} />
          <List
            todos={todos}
            showTodos={showTodos}
            editTodos={this.editTodos}
            updateTodo={this.updateTodo}
			deleteTodo={this.deleteTodo}
			filter={this.state.filter}
          />
          <Footer
            todos={todos}
            clearAllDone={this.clearAllDone}
            allDone={this.allDone}
            allTodos={this.allTodos}
            allNoDone={this.allNoDone}
          />
        </div>
      </div>
    );
  }
}
