import React, { Component } from "react";
import Header from "./components/Header";
import List from "./components/List";
import Footer from "./components/Footer";
import "./App.css";
export default class App extends Component {
  //初始化状态
  state = {
    todos: [
      { id: "001", name: "a", done: true, sign: "3" },
      { id: "002", name: "b", done: true, sign: "2" },
      { id: "003", name: "c", done: false, sign: "3" },
      { id: "004", name: "d", done: false, sign: "1" },
    ],
    filter: "all",
    pastodos: [
      { id: "002", name: "bc", done: true, sign: "2" },
      { id: "002", name: "bc", done: true, sign: "1" }
    ],
    featuretodos: [],
  };
  //添加
  add = (todoObj) => {
    const { todos } = this.state;
    const newTodos = [todoObj, ...todos];
    this.setState({ todos: todoObj });
    this.setState({ todos: newTodos });
  };
  //更新
  updateTodo = (id, done) => {
    //获取状态中的todos
    const { todos } = this.state;
    //匹配处理数据
    const newTodos = todos.map((todoObj) => {
      if (todoObj.id === id) return { ...todoObj, done };
      else return todoObj;
    });

    this.setState({ todos: newTodos });
  };
  //删除
  deleteTodo = (id) => {
    const { todos } = this.state;
    const newTodos = todos.filter((todoObj) => {
      return todoObj.id !== id;
    });
    this.setState({ todos: newTodos });
  };

  //删除已完成
  clearAllDone = () => {
    const { todos } = this.state;
    const newTodos = todos.filter((todoObj) => {
      return !todoObj.done;
    });
    this.setState({ todos: newTodos });
  };
  //显示已完成
  allDone = () => {
    this.setState({ filter: "complete" });
  };
  //全部
  allTodos = () => {
    this.setState({ filter: "all" });
  };
  //未完成
  allNoDone = () => {
    this.setState({ filter: "undone" });
  };
  //编辑
  editTodos = (id, name) => {
    const { todos } = this.state;
    const newTodos = todos.map((todoObj) => {
      if (todoObj.id === id) return { ...todoObj, name };
      else return todoObj;
    });
    this.setState({ todos: newTodos });
  };
  //优先级修改
  changeSign = (id, sign) => {
    const { todos } = this.state;
    //匹配处理数据
    const newTodos = todos.map((todoObj) => {
      if (todoObj.id === id) return { ...todoObj, sign };
      else return todoObj;
    });
    this.setState({ todos: newTodos });
  };
  //排序
  sortBySign = () => {
    const { todos } = this.state;
    const newTodos = todos.sort((a, b) => {
      return b.sign - a.sign;
    });
    this.setState({ todos: newTodos });
  };
  //撤销
  beforEdit = () => {
    const { pastodos, todos,featuretodos } = this.state;
    const newTodo = pastodos.pop()
    featuretodos.push(newTodo)
    if (newTodo == null) {
      return 
    } else {
      const id = newTodo.id;
      const newTodos = todos.map((todoObj) => {
        if (todoObj.id === id) return { ...todoObj, ...newTodo };
        else return todoObj;
      });
      this.setState({ todos: newTodos });
    }
  };
  //前进
  feature =()=>{
    const { pastodos, todos,featuretodos } = this.state;
    pastodos.push()
  }
  render() {
    const { todos, showTodos, pastodos } = this.state;
    return (
      <div className="todo-container">
        <div className="todo-wrap">
          <Header add={this.add} sortBySign={this.sortBySign} />
          <List
            todos={todos}
            showTodos={showTodos}
            editTodos={this.editTodos}
            updateTodo={this.updateTodo}
            deleteTodo={this.deleteTodo}
            filter={this.state.filter}
            changeSign={this.changeSign}
          />
          <Footer
            todos={todos}
            pastodos={pastodos}
            beforEdit={this.beforEdit}
            clearAllDone={this.clearAllDone}
            allDone={this.allDone}
            allTodos={this.allTodos}
            allNoDone={this.allNoDone}
          />
        </div>
      </div>
    );
  }
}
