import React, { Component } from 'react'
import './index.css'

export default class Footer extends Component {

    //清除已完成
    handleClearAllDone = ()=>{
		this.props.clearAllDone()
    }
    handleAllTodos=()=>{
        this.props.allTodos()
    }
    handleAllDone=()=>{
        this.props.allTodos()
    }
    render(){
        const{todos}=this.props
        //总
        const total =todos.length
        //已完成
    const doneTodos = todos.reduce((pre,curr)=>{return pre + (curr.done ? 1 : 0 )},0)
        return(
            <div className="todo-footer">
				<span>
                    <span>已完成 {doneTodos}</span> / 全部 {total}
				</span>
                <button onClick={this.handleAllTodos} className="btn btn-danger">全部</button>
                <button onClick={this.handleAllDone} className="btn btn-danger">已完成</button>
                <button  className="btn btn-danger">未完成</button>
                <button onClick={this.handleClearAllDone}  className="btn btn-danger" >清除已完成任务</button>

			</div>
        )
    }
}
